// Deconvolute.cpp : Defines the entry point for the console application.////#include <boost/iostreams/filter/gzip.hpp>//#include <boost/iostreams/filtering_stream.hpp>//#include <boost/iostreams/filter/zlib.hpp>#include <fstream>#include <vector>#include "dirent.h"#include <string>#include "Engine.h"int main(int argc, char *argv[]){	vector<string> arguments;	if (argc != 6)	{		cout << "Wrong parameter number\n";				for (int index = 0; index < argc; index++)		{			cout << argv[index] << "\n";		}				cout << "Wrong number of arguments, usage:\n read one filename with path,\n read two filename with path,\n path to primer file,\n path to index file\n and path to folder to save data in\n";		return -1;	}	for (int index = 1; index < argc; index++)	{		arguments.push_back(argv[index]);	}	Engine eng(arguments);	eng.AnalyseData();	return 0;}